import {
  Button,
  Card,
  CardContent,
  CardMedia,
  TextField,
  Typography,
} from "@mui/material";
import dayjs from "dayjs";
import React, { useCallback, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useAppDispatch, useAppSelector } from "../../app/hooks";
import Spinner from "../../components/Spinner/Spinner";
import { BaseUrl } from "../../constants";
import { selectUser } from "../../features/user/userSlice";
import {
  addComment,
  commentsArray,
  getComments,
  getOnePosts,
  PostOne,
} from "../../store/ForumSlice";

const Comments = () => {
  const [comment, setComment] = useState({
    text: "",
  });
  const user = useAppSelector(selectUser);
  const comments = useAppSelector(commentsArray);
  const { id } = useParams();
  const dispatch = useAppDispatch();
  const onePost = useAppSelector(PostOne);

  const getOnePost = useCallback(async () => {
    await dispatch(getOnePosts(id!));
    await dispatch(getComments(id!));
  }, [dispatch, id]);

  useEffect(() => {
    getOnePost().catch(console.error);
  }, [getOnePost]);

  let cardImage =
    "data:image/png;base64,";

  if (onePost.image) {
    cardImage = BaseUrl + "/" + onePost.image;
  }

  const createCard = comments.map((element) => {
    return (
      <Card sx={{ minWidth: 275, my: 4 }} key={element._id}>
        <CardContent>
          <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
            {element.name}
          </Typography>
          <Typography sx={{ mb: 1.5 }} color="text.secondary">
            {element.text}
          </Typography>
        </CardContent>
      </Card>
    );
  });

  const onCardChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;

    setComment((prev) => ({ ...prev, [name]: value }));
  };

  let onFormSubmit = async (event: React.FormEvent) => {
    event.preventDefault();
    const object = {
      text: comment.text,
      id: id!,
    };
    await dispatch(addComment(object));
    await dispatch(getComments(id!));
    setComment({ text: "" });
  };

  return onePost.title !== "" ? (
    <div>
      <Card sx={{ maxWidth: 345 }}>
        <CardMedia
          component="img"
          alt="green iguana"
          height="140"
          image={cardImage}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            {onePost.title}
          </Typography>
          <Typography gutterBottom variant="h5" component="div">
            {user?.name}
          </Typography>

          <Typography variant="body2" color="text.secondary">
            {onePost.description}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {dayjs(onePost.datetime).format("DD.MM.YYYY HH:mm:ss")}
          </Typography>
        </CardContent>
        {user ? (
          <div>
            <form onSubmit={onFormSubmit}>
              <TextField
                id="outlined-basic"
                label="Comment"
                variant="outlined"
                required
                name="text"
                value={comment.text}
                onChange={onCardChange}
              />
              <Button variant="contained" type="submit">
                Post
              </Button>
            </form>
          </div>
        ) : (
          <div></div>
        )}
      </Card>
      {createCard}
    </div>
  ) : (
    <Spinner />
  );
};

export default Comments;
